/**
 * @function getBlocksFromSheet
 * @param {string} sheetName - "action"
 * @param {string} spreadsheetId - "spreadsheetId"
 * @param {string} value - "create-project"
 * @return {string or null} 있으면 가져오고 없으면 null, blocks는 JSON.parse 를 통해 엑세스 
 * @description 특정 시트에서 blocks 를 가져옴
 */
function getBlocksFromSheet(sheetName, spreadsheetId, value){
  var spreadsheet = SpreadsheetApp.openById(spreadsheetId);
  // sheet 리스트
  var sheet = spreadsheet.getSheetByName(sheetName);
  // 데이터가 실제로 있는 마지막 행을 찾습니다.
  var lastRow = sheet.getLastRow();
  // 데이터 범위 순회
  var blocks;
  for(var row=1; row<lastRow+1; row++){
    if (sheet.getRange("A"+String(row)).getValue() === value){
      blocks = sheet.getRange("B"+String(row)).getValue();
    }
  }
  return blocks;
}
